mapper.select:
SELECT *
FROM tag_info
WHERE user_id = #{userId}
  AND book_id = #{bookId}
  AND is_delete = 0
ORDER BY sort_order ASC, created_at ASC

SELECT *
FROM tag_info
WHERE user_id = #{userId}
  AND book_id = #{bookId}
  AND tag_id IN

    #{t}

update.foreach:
UPDATE tag_info
SET name = #{t.name},
    color = #{t.color},
    sort_order = #{t.sortOrder},
    is_delete = #{t.isDelete},
    update_time = #{t.updateTime}
WHERE id = #{t.id}
  AND user_id = #{t.userId}

mapper.update:
UPDATE tag_info
SET is_delete = 1,
    update_time = NOW()
WHERE user_id = #{userId}
  AND book_id = #{bookId}
  AND tag_id = #{tagId}
